import useBasic from "../hooks/use-basic";

const BasicForm = (props) => {
  const {
    value: enteredFNameValue,
    isValid: fNameValueIsValid,
    hasError: fNameHasError,
    valueChangeHandler: fNameChange,
    inputBlurHandler: fNameInputBlur,
    reset: resetFNameInput,
  } = useBasic((value) => value.trim().length === 0);

  let formIsValid = false;

  if (fNameValueIsValid) {
    formIsValid = true;
  }

  const formSubmissionHandler = (event) => {
    event.preventDefault();

    if (!fNameValueIsValid) return;

    resetFNameInput();
  };

  return (
    <form onSubmit={formSubmissionHandler}>
      <div className="control-group">
        <div className="form-control">
          <label htmlFor="name">First Name</label>
          <input
            type="text"
            id="name"
            value={enteredFNameValue}
            onChange={fNameChange}
            onBlur={fNameInputBlur}
          />
          {fNameHasError && <p className="error-text">First Name value is invalid.</p>}
        </div>
        <div className="form-control">
          <label htmlFor="name">Last Name</label>
          <input type="text" id="name" />
        </div>
      </div>
      <div className="form-control">
        <label htmlFor="name">E-Mail Address</label>
        <input type="text" id="name" />
      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
