import React, { useState } from 'react';

import ExpenseForm from './ExpenseForm';
import ExpenseCancel from './ExpenseCancel';
import './NewExpense.css';

const NewExpense = (props) => {
    const [changedCancel, setChangedCancel] = useState(false);

    const saveExpenseDataHandler = (enteredExpenseData) => {
        const expenseData = {
            ...enteredExpenseData,
            id : Math.random().toString()
        }; 

        setChangedCancel(expenseData.cancel);

        props.onAddExpense(expenseData);

        console.log(expenseData);
    };

    const saveChangedDataHandler = (changedCancelData) => {
        const changeData = {
            ...changedCancelData
        }

        setChangedCancel(changeData.cancel);

        console.log('saveChangedDataHandler',changeData.cancel)
    }

    const expenseCancel = <ExpenseCancel cancel={changedCancel} onChangedCancelData={saveChangedDataHandler}/>;


    return <div className='new-expense'>
        <button>Add New Expense</button>
        <ExpenseForm className="new-expense__expense-form" onSaveExpenseData={saveExpenseDataHandler}/>
    </div>;
}

export default NewExpense;
